class Car:
    def __init__(self, brand, color):
        self.brand = brand
        self.color = color

    def start_engine(self):
        print(f"{self.brand} গাড়িটি স্টার্ট হয়েছে...")

# অবজেক্ট তৈরি
my_car = Car("Toyota", "White")
my_car.start_engine()

class Animal:
    def speak(self):
        print("প্রাণীটি শব্দ করছে...")

class Dog(Animal):
    def speak(self):
        print("ঘেউ ঘেউ!")

class Cat(Animal):
    def speak(self):
        print("মিয়াউ!")

d = Dog()
d.speak()  # আউটপুট: ঘেউ ঘেউ!

try:
    number = int(input("একটি সংখ্যা দিন: "))
    result = 100 / number
    print(result)
except ZeroDivisionError:
    print("ভুল! শূন্য দিয়ে ভাগ করা যায় না।")
except ValueError:
    print("ভুল! দয়া করে সংখ্যা লিখুন।")

import random

def guessing_game():
    secret_number = random.randint(1, 100)
    attempts = 0

    print("স্বাগতম! ১ থেকে ১০০ এর মধ্যে সংখ্যাটি অনুমান করুন।")

    while True:
        try:
            guess = int(input("আপনার অনুমান: "))
            attempts += 1

            if guess < secret_number:
                print("আরও বড় সংখ্যা!")
            elif guess > secret_number:
                print("আরও ছোট সংখ্যা!")
            else:
                print(f"অভিনন্দন! আপনি {attempts} বারে সঠিক সংখ্যা ({secret_number}) ধরে ফেলেছেন!")
                break
        except ValueError:
            print("দয়া করে একটি বৈধ সংখ্যা লিখুন।")

# গেম শুরু করুন
guessing_game()
